loc_mex_05_01 = "เอาหน่อยน่า ลองหาดู",--Come on you chops, keep looking.
loc_mex_15_03 = "ไปได้สวย กล้องไปเรียบร้อยแล้ว",--Nice going, taking out the cameras.
